import streamlit as st
import math

# halaman utama
st.title('aplikasi perhitungan luas bangun datar')
st.header('ini buatan anak SI')

menu = st.sidebar.selectbox('menu',['luas persegi','luas segitiga','luas lingkaran', ' luas persegi panjang ',' luas jajar genjang ',' luas belah ketupat '])
if menu == 'luas persegi':
    st.write('ini halaman untuk menghitung luas persegi')
    st.markdown(':red[luas persegi]')
    st.image('https://tse1.mm.bing.net/th/id/OIP.tb5ldcpW9pqz82ERPhkvDwAAAA?w=180&h=181&c=7&r=0&o=7&cb=ucfimg2&dpr=1.5&pid=1.7&rm=3&ucfimg=1', caption='gambar persegi')
    sisi = st.number_input('silahkan masukkan sisi', min_value=0, )
    if st.button('hitung'):
        luas = sisi * sisi
        st.write(f'luas persegi adalah {luas}')

if menu == 'luas segitiga':
    st.write('ini halaman untuk menghitung luas segitiga')
    st.markdown(':red[luas segitiga]')
    st.image('data:image/png;base64,', caption='gambar segitiga')
    alas = st.number_input('masukan alas segitiga', min_value= 0,)
    tinggi = st.number_input('masukan tinggi segitiga', min_value= 0,)
    if st.button('hitung'):
        luas = 0.5 * alas * tinggi 
        st.write(f'luas segitiga adalah {luas}')

if menu == 'luas lingkaran':
    st.write('ini halaman untuk menghitung luas lingkaran')
    st.markdown(':red[luas lingkaran]')
    st.image('data:image/webp;base64,', caption= 'gambar lingkaran')
    jarijari = st.number_input(' masukkan jarijari lingkaran',min_value=0,)
    if st.button('hitung'):
        luas = math.pi * jarijari * jarijari
        st.write(f'luas lingkaran adalah {luas}')
      
    
if menu == ' luas persegi panjang ':
    st.write('ini halaman untuk menghitung luas persegi panjang')
    st.markdown(':red[luas persegi panjang]')
    st.image('data:image/webp;base64,', caption='gambar persegi panjang')
    panjang = st.number_input('masukan panjang persegi panjang', min_value= 0,)
    lebar = st.number_input('masukan lebar persegi panjang', min_value= 0,)
    if st.button('hitung'):
        luas = panjang * lebar
        st.write(f'luas persegi panjang adalah {luas}')

if menu == ' luas jajar genjang ':
    st.write('ini halaman untuk menghitung luas jajar genjang')
    st.markdown(':red[luas jajar genjang]')
    st.image('data:image/webp;base64,', caption = 'gambar jajar genjang')
    alas = st.number_input('masukan alas jajar genjang', min_value= 0,)
    tinggi = st.number_input('masukkan tinggi jajar genjang', min_value=0,)
    if st.button('hitung'):
        luas = alas * tinggi
        st.write(f'luas jajar genjang adalah {luas}')


if menu == ' luas belah ketupat ':
    st.write('ini halaman untuk menghitung luas belah ketupat')
    st.markdown(':red[luas belah ketupat]')
    st.image('data:image/webp;base64,', caption = 'gambar belah ketupat')
    diagonal1 = st.number_input('masukan diagonal pertama belah ketupat', min_value= 0,)
    diagonal2 = st.number_input('masukkan diagonal kedua belah ketupat', min_value=0,)
    if st.button('hitung'):
        luas = 0.5 * diagonal1 * diagonal2
        st.write(f'luas belah ketupat adalah {luas}')

